mapper.insert:
DECLARE
    V_BOARD_NO NUMBER;
BEGIN
    SELECT MAX(BOARD_NO) + 1 INTO V_BOARD_NO FROM T_BOARD_LIST;

    INSERT INTO T_BOARD_LIST
    SELECT V_BOARD_NO,
           #{board_title},
           #{board_user_id},
           TO_CHAR(SYSDATE, 'YYYYMMDDHH24mmss'),
           TO_CHAR(SYSDATE, 'YYYYMMDDHH24mmss'),
           #{board_content}
      FROM DUAL;
END;
